    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
try {
            Class.forName("com.mysql.jdbc.Driver");
        Connection connection =DriverManager.getConnection("jdbc:mysql://localhost:3306/clientinfodb","root", "root");
        Statement stmnt=connection.createStatement();
        ResultSet rs=stmnt.executeQuery("SELECT * from  client where id="+jTextField1.getText());
        if(rs.next())
        {
            setrest.setText("Already Exist");
            jTextField1.setText("");
            jTextField2.setText("");
            jTextField3.setText("");
            sucess.setText("");
        }
        else {
            String id=jTextField1.getText();
            String name=jTextField2.getText();
            String email=jTextField3.getText();
            
            String query="insert into clientinfodb.client(ID,Name,Email) values ('"+id+"','"+name+"','"+email+"')";
            try{
        Statement stmnt2=connection.createStatement();
        stmnt2.executeUpdate(query);
        sucess.setText("Executed");
            }
            catch(Exception e)
                        {e.printStackTrace();
            }
            
            
        }
}
         catch (Exception e)
        { e.printStackTrace();
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

